// carrot
$(document).ready(function(){
	$('ul').on('click','.sent img', function(e){
		e.preventDefault();
		var id = $(this).attr('id');
		console.log(id);

		  var settings = {
		  "async": true,
		  "crossDomain": true,
		  "url": "https://tagchatter.herokuapp.com/messages/"+id+"/parrot",
		  "method": "PUT",
		  "headers": {
		    "Content-Type": "application/json",
		    "Accept": "*/*",
		    "Cache-Control": "no-cache"
		  },
		  "processData": false,
		  "data": "{\n    \"message\": \"Hello World!-BR\",\n    \"author_id\": \"laverne_jacobi11\"\n}"
		}

		$.ajax(settings).done(function (response) {
		  console.log(response);
		});

	
	});

});








